INSERT INTO `profile` (`id`, `profile_image_id`, `name`, `description`, `public`, `url`, `location`, `banner_image_id`, `quotes`, `meta_description`, `genre`, `short_name`, `banner_video_path`, `youtube_link`) VALUES
  (1, 1, 'Barthy', '<p>Barthy interessiert sich nicht blo&szlig; f&uuml;r die Musik, sondern zus&auml;tzlich f&uuml;r alles drumherum.<br />\r\nEr begann erstmals 2012 an instrumentaler Musik zu schrauben, an Texten zu feilen und sich gleichzeitig f&uuml;r Wege zu interessieren, wie er sein Schaffen unter die Leute bringen kann. Sich mit SoundCloud und &auml;hnlichen Portalen nicht zufrieden gebend, wurden prompt eigene Seiten entworfen und somit auch der Gedanke gefasst, Musik und den guten Zweck zu verbinden.<br />\r\n<br />\r\nNach k&uuml;nstlerischen T&auml;tigkeiten, die von Musikkomposition &uuml;ber Bildgestaltung bis hin zum Webdesign reichen, gr&uuml;ndlicher organisatorischer Vorbereitung, einem dutzend Freunden, die schon &uuml;ber das Projekt dr&uuml;berschauen und es mitgestalten durften, pr&auml;sentiert sich hier sein neues Projekt: keinegrenzen.org</p>', 1, 'barthy', 'Köln', 6, '<p><em>Ohne zu wissen wie der Winter ist, w&uuml;rden wir den Sommer vermissen?</em></p>\r\n\r\n<p><em>Der n&auml;chste einzuleitende Schritt ist mir einen Kasten Club Mate zu holen. Dann sehen wir weiter.</em></p>', 'Barthy''s Profil auf keinegrenzen.org - Elektronische Musik aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Electronic', NULL, NULL, NULL),
  (3, 5, 'INVO', '<p>Invo schreibt meistens Texte &uuml;ber Frauen, manchmal findet jedoch auch ein Storyteller oder ein bisschen k&ouml;lner representer Rap seinen Weg ins Mikrofon.<br />\r\nDer junge gelernte Tontechniker strebt nach einem Politikstudium und etabliert sich peu &agrave; peu in der Musikszene. Mit vielen Songs die bereits mehrere Tausend Plays auf YouTube, SoundCloud und Co. auf dem Z&auml;hler haben&nbsp;hat er sich bereits einen Namen gemacht.</p>\r\n\r\n<p>Der k&ouml;lsche Freigeist erm&ouml;glicht es ihm stets Leute zu begeistern, zu unterhalten und vor allem zu &uuml;berzeugen.</p>', 1, 'invo', 'Köln', 7, '<p><em>Ich fasse mich kurz, weil ich nichts kompensieren muss.</em><br />\r\n<br />\r\n<em>Am Anfang war das Wort. Dann hat es ihm die Sprache verschlagen.</em></p>', 'INVO''s Profil auf keinegrenzen.org - Deutschrap aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Rap', NULL, NULL, NULL),
  (4, 11, 'ENNA', '<p>Nils rappt &uuml;ber Beats von seinem Mitbewohner Raban und hat gerne was zu sagen. Man kann sich sicher sein, dass es in seinen Songs tats&auml;chlich um etwas geht.<br />\r\nDer K&ouml;lner Student erreicht seine Generation (Stichwort #studentlife) mit Themen, die jeden zum Lachen oder Nachdenken bringen.<br />\r\n<br />\r\nDie Musiker-WG l&auml;dt regelm&auml;&szlig;ig kristallklar abgemischte und technisch versierte Songs ins Netz und stellt ihr K&ouml;nnen unter Beweis!</p>', 1, 'enna', 'Köln', 12, '<p><em>Wenn ich was anziehen will muss ich vorher in der W&auml;sche w&uuml;hlen...</em></p>\r\n\r\n<p><em>Vielleicht bin ich gar nicht pleite sondern Manuel Neuer.</em></p>', 'ENNA''s Profil auf keinegrenzen.org - Deutschrap aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Rap', NULL, 'uploads/videos/enna.mp4', NULL),
  (5, 15, 'Waking Up In Stereo', '<p>Die Br&uuml;der Arin &amp; Armen aus K&ouml;ln kombinieren deepen Techno mit eingehenden Pop-Sounds und bringen es zusammen als &quot;Waking Up In Stereo&quot;, oder kurz &quot;WUIS&quot;&nbsp;auf die B&uuml;hne.</p>\r\n\r\n<p>Als musikalisch sehr begabtes und eingespieltes Team begeistern sie die Zuschauer, die zwischen seligem Lauschen und Tanzen auf dicken elektronischen Beats hin- und hergerissen werden.<br />\r\nIn ihren Songs spiegelt sich die Liebe und Hingabe zur Musik wider, gepaart mit Spa&szlig; am Experimentieren. So entsteht ein ganz eigener Sound, der unheimlich viel Spa&szlig; macht!</p>', 1, 'wuis', 'Köln', 16, NULL, 'WUIS'' Profil auf keinegrenzen.org - Die Elektro-Brüder aus Köln und ihre Musik für einen guten Zweck. Jetzt anhören und spenden!', 'Electronic', 'WUIS', 'uploads/videos/wuis.mp4', NULL),
  (7, 20, 'Suburbian Rex', '<p>Die K&ouml;nige der Vorstadt&nbsp;Anton &#39;Anthony&#39; Fatjanov&nbsp;(Gitarre, Vocals), Ivo Althaus &#39;Ovi Oldhouse&#39; (Bass) - und Julian &#39;Julio&#39; Heck (Drums) stehen seit M&auml;rz 2016 auf der B&uuml;hne.<br />\r\nIhre Musik bezeichnen Sie selbst als &quot;Rockmusik/Manchester Indie alias Present-Age Rock&quot;, wem das zu kompliziert ist kann sich auch mit &quot;Vom Feuerzeug bis zum Moshpit ist alles drin&quot; begn&uuml;gen.<br />\r\n<br />\r\nDie Souver&auml;nit&auml;t auf der B&uuml;hne macht ihrem Bandnamen alle Ehre, was sicherlich auch an ihrer&nbsp;langj&auml;hrigen&nbsp;Freundschaft liegt, aus der die Band heraus gegr&uuml;ndet wurde. Sie stellen sich gegen kommerzielle Radio-Musik und wollen Rock&#39;n&#39;Roll der Gesellschaft wieder beibringen.&nbsp;<br /><br/>\r\n<small>Foto (Banner): &copy;<a href="http://ausdrucksstark-fotografie.de/" rel="nofollow" target="_blank">Patricia Heck, Ausdrucksstark - Fotografie &amp; Medienentwicklung </a></small></p>', 1, 'suburbian-rex', 'Köln/Jülich', 21, '<p><em>My generation burdened with expectation<br />\r\nNo one remembers John&#39;s imagination</em></p>', 'Suburbian Rex auf keinegrenzen.org - Rock''n''Roll für einen guten Zweck. Jetzt anhören und spenden!', 'Rock', NULL, NULL, NULL),
  (8, 26, 'Aapman', '<p>Der junge und&nbsp;talentierte Familienvater Emil sieht das Musizieren&nbsp;als pure Leidenschaft. Schon in jungen Jahren sa&szlig; er am Klavier und l&auml;sst seitdem&nbsp;seiner Kreativit&auml;t freien lauf.&nbsp;Nach und nach&nbsp;auch an der Gitarre, am Bass, an der Trompete und der Posaune.<br />\r\nUm seine Liebe zur Musik vollst&auml;ndig beschreiben zu k&ouml;nnen, m&uuml;ssen lediglich noch zwei Dinge gesagt werden: Erstens, Emil a.k.a. Aapman lernt und arbeitet&nbsp;im&nbsp;Beruf des Klavierbauers. Und zweitens sei das einzig sch&ouml;nere vor der Musik&nbsp;seine kleine Familie, wie er selber sagt.<br />\r\nDas zusammen klingt in meinen Ohren&nbsp;ein kleines bisschen perfekt.</p>', 1, 'aapman', 'Köln', 24, NULL, 'Aapmans Profil auf keinegrenzen.org - Deutschrap aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Rap', NULL, NULL, NULL),
  (9, 27, 'Kokee Thornton', '<p>Musik, Comics und Bilder geh&ouml;ren zu Kokees Alltag. Ihre Comicfiguren, die &quot;Weirdoz&quot;, sind stets auf der Suche nach Harmonie oder stehen ganz gew&ouml;hnlichen Problemen auf unglaublich niedliche Art und Weise gegen&uuml;ber. Wer heute noch nicht gel&auml;chelt hat, <a href="https://www.instagram.com/kokeethornton/" target="_blank" title="Kokee Thornton auf Instagram">sollte schnellstm&ouml;glich ihre Instagram-Seite besuchen!</a>&nbsp;<br />\r\n<br />\r\nKokees&nbsp;Musik passt auf die Comics wie die Faust aufs Auge und erschafft damit ein interessantes und individuelles Gesamtwerk!&nbsp;</p>', 1, 'kokee-thornton', 'Lüneburg', 28, NULL, 'Kokee Thornton''s Profil auf keinegrenzen.org - Hip-Hop aus Lüneburg für einen guten Zweck. Jetzt anhören und spenden!', 'Hip-Hop', NULL, NULL, NULL),
  (11, 33, 'Paul Maerz', '<p>Paul Maerz, der eigentlich weder Paul noch Maerz, sondern Wenzel Rehbach hei&szlig;t, beschreibt sich selbst viel besser, als jeder andere es k&ouml;nnte:<br />\r\n<br />\r\n<em>&rdquo;Mich inspirieren NASA-Youtubevideos, Musik von &#39;Hot Sugar&#39;,&nbsp;Stephen Hawking,&nbsp;gr&uuml;ner Tee,&nbsp;Robert Capa,&nbsp;Mundharmonica-Tutorials,&nbsp;Caspar David Friedrich,&nbsp;Dokumentarfilme,&nbsp;Leerkassetten und Microsoft Paint.&quot;</em><br />\r\n<br />\r\nDer junge K&ouml;lner studiert Philosophie und Germanistik, m&ouml;chte sich aber eigentlich mit einem Kunst-Studium voll und ganz der Kreativit&auml;t hingeben.</p>', 1, 'paulmaerz', 'Köln', 34, NULL, 'Paul Maerz'' Profil auf keinegrenzen.org - Hip-Hop aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Hip-Hop', NULL, NULL, NULL),
  (12, 36, 're:loom', '<p>Wenn die vier talentierten K&ouml;pfe von re:loom die B&uuml;hne betreten, nehmen sie die Vibes des Publikums, die Inspiration des Moments und die Liebe zur Kreativit&auml;t und wandeln sie in spontane, improvisierte Songs um.<br />\r\nDabei entsteht spannender Electro-Pop mit Einfl&uuml;ssen aus Jazz und Soul, oftmals bereichert durch Gastmusiker.<br />\r\n<br />\r\n<small>Foto (Profilbild): &copy;<a href="http://www.smashingsnapshots.de/" rel="nofollow" target="_blank"> Lena Semmelroggen</a></small></p>', 0, 'reloom', 'Köln', 37, NULL, 're:loom''s Profil auf keinegrenzen.org - Electro-Pop aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Electro-Pop', NULL, NULL, NULL),
  (13, 39, 'CML & Assim', '<p>Als Teil des Musik-Labels und Projektes&nbsp;<a href="http://www.s1mrecords.de/" rel="nofollow" target="_blank">&quot;sei1mensch&quot; (oder &quot;S1M Records&quot;)</a>&nbsp;stehen CML und Assim f&uuml;r deutschen Rap, der mehr bieten soll als die &uuml;blichen Floskeln.<br />\r\nPhilosophische Gedanken und Spiritualit&auml;t bieten eine Basis, sich&nbsp;von Klischees zu befreien, umgesetzt&nbsp;mit ausgefeilten Flows und ganz eigenem Charakter.</p>', 0, 'sei1mensch', 'Köln', 40, NULL, 'CML & Assim''s Profil auf keinegrenzen.org - Deutschrap aus Köln für einen guten Zweck. Jetzt anhören und spenden!', 'Rap', NULL, NULL, NULL),
  (14, 42, 'Jabaroo', '<p>Jabaroo - benannt nach einem schwarzhalsigem, australischem&nbsp;Storch - stehen als Band f&uuml;r exotische Weltmusik. Sobald die ersten Kl&auml;nge ert&ouml;nen, sieht der Zuh&ouml;rer sich entf&uuml;hrt in eine Traumreise &uuml;ber W&uuml;sten, Schluchten&nbsp;und weiten Graslandschaften.<br />\r\nGesang und Sprache werden begleitet von feinf&uuml;hligen Melodien auf der Harfe, Fl&auml;chen und Atmosph&auml;ren aus Gitarre, Bass und Didgeridoo, Drums und Percussions&nbsp;und vermutlich noch vielem mehr.</p>', 1, 'jabaroo', 'Oberberg', 43, NULL, 'Jabaroo''s Profil auf keinegrenzen.org - Weltmusik aus Oberberg für einen guten Zweck. Jetzt anhören und spenden!', 'Weltmusik', NULL, NULL, NULL);
INSERT INTO `album` (`id`, `cover_id`, `title`, `description`, `published`, `url`, `profile_id`, `background_color`, `primary_color`, `secondary_color`, `sound_cloud_link`, `album_file_path`, `downloads`) VALUES
(2, 9, 'Musik Ohne Grenzen', '<p>Musik Ohne Grenzen ist Barthys Deb&uuml;t-Album.<br />\r\nEs erschien gleichzeitig mit der ersten Version dieses Projektes.<br />\r\nDas Album ist voll von Elektronischen Beats, untermalt mit Streichern und Pianos. In ihr finden sich unter anderem Einfl&uuml;sse aus Chillout, Dub, Electro, Ambient und New Age.<br />\r\nBarthy nutzte die Zeit w&auml;hrend seines freiwilligen sozialen Jahres das Album mit viel Liebe zu produzieren.</p>', '2014-04-04', 'mwb', 1, 'rgb(141, 174, 131)', 'rgb(255, 255, 255)', 'rgb(150, 14, 13)', 'https://soundcloud.com/barthy-b/sets/music-without-borders', 'Barthy_MWB.zip', 14),
(3, 10, 'Schall & Rauch EP', '<p>INVO&nbsp;hat seine&nbsp;erste&nbsp;EP gemeinsam mit Produzent Mighty Moe auf die Beine gestellt. Die Beats sind gesamplet und haben allesamt Kopfnicker-Potenzial.<br />\r\nAufgenommen im Keller zwischen Schwarzlichtr&ouml;hren und einem &quot;Inglorius Basterds&quot; Poster hat diese EP sehr viel Underground-Charakter.<br />\r\nKaum war die &quot;Schall &amp; Rauch EP&quot; aus den Startl&ouml;chern, k&uuml;ndigte INVO die n&auml;chste an: &quot;Melonie&quot; wird sie hei&szlig;en und wird alles bisherige dank Erfahrung, Professionalit&auml;t und vor allem Leidenschaft in den Schatten stellen. Man darf gespannt sein!</p>', '2016-04-27', 'schall-rauch-ep', 3, 'rgb(5, 6, 8)', 'rgb(69, 105, 178)', 'rgb(199, 200, 202)', 'https://soundcloud.com/invorap/sets/schall-rauch-ep', 'INVO_Schall_Und_Rauch_EP.zip', 19),
(4, 13, 'Halb Mensch Halb Plan', '<p>Der Name ist Programm - ENNA sucht in 5 Songs nach Wegen und Mitteln ein funktionierender Mensch zu werden.<br />\r\nDie Beats unterst&uuml;tzen ihn&nbsp;dabei mit einer hohen Diversit&auml;t: von brachialen Dubstep-Wobbles zur&nbsp;Akustikgitarre ist das musikalische&nbsp;Spektrum auf sehr stimmige Weise breit gef&auml;chert.<br />\r\nENNA unterstreicht seine aussagekr&auml;ftigen Texte mit gut durchdachten Metaphern und kraftvollen, eing&auml;ngigen Hooks.</p>', '2016-04-05', 'halb-mensch-halp-plan', 4, 'rgb(176, 175, 174)', 'rgb(25, 24, 28)', 'rgb(158, 39, 40)', 'https://soundcloud.com/ennasound/sets/halb-mensch-halb-plan', 'ENNA_Halb_Mensch_Halb_Plan_EP.zip', 21),
(6, 17, 'WUIS', '<p>Die Deb&uuml;t-EP &quot;WUIS&quot; enth&auml;lt zwei v&ouml;llig verschiedene aber dennoch wunderbar einheitliche Songs. &quot;Gallery of Memory&quot; bewegt mit starken B&auml;ssen und ausgefeilten Drums, w&auml;hrend &quot;Era Error&quot; mit gef&uuml;hlvollem Gesang und gute Laune bringenden Melodien &uuml;berzeugt.<br />\r\nDie EP gibt es au&szlig;er in digitaler Form ebenfalls auf einer klassischen 7-Zoll Vinyl Platte&nbsp;und da passt sie, wenn man mal ehrlich ist, auch verdammt gut drauf.&nbsp;</p>', '2016-01-22', 'wuis-ep', 5, 'rgb(24, 28, 40)', 'rgb(209, 211, 210)', 'rgb(151, 186, 193)', 'https://soundcloud.com/wakingupinstereo/sets/wuis/s-7Ei9d', 'Waking_Up_In_Stereo_WUIS.zip', 25),
(7, 18, 'Melonie EP', '<p>Frauen und Hip-Hop sind nicht nur Inhalt des Covers, sondern auch von INVOs kreativem Kopf.<br />\r\nF&uuml;r ihn bedeutet es&nbsp;Harmonie und Melodie (= Melonie), was mehr ein Lebensgef&uuml;hl als ein Wortspiel ist.<br />\r\nDie Melonie EP &uuml;berzeugt mit lyrischen Finessen, durchdachten Flows und zum Kopfnicken antreibenden Beats.</p>', '2016-10-06', 'melonie', 3, 'rgb(255, 255, 255)', 'rgb(55, 56, 65)', 'rgb(181, 70, 37)', 'https://soundcloud.com/invorap/sets/melonie-ep', 'INVO_Melonie_EP.zip', 19),
(8, 19, 'Generation Omega', '<p>Wer ist die Generation Omega? Sind es die, die zu viele Erwartungen an sich selbst haben? Die, die zu faul und unmotiviert sind? Oder sind es alle die, die schnell und einfach etwas gro&szlig;es und beibendes erschaffen wollen? Vermutlich all das und noch viel viel mehr. Wer wei&szlig; das schon.<br />\r\nENNA spricht, rappt und singt&nbsp;genau dar&uuml;ber was es bedeutet aus zahllosen M&ouml;glichkeiten die eine richtige zu finden oder&nbsp;sich selber pr&auml;sentieren zu wollen, ohne jegliche Ahnung zu haben, wer man eigentlich ist.</p>', '2017-01-31', 'generation-omega', 4, 'rgb(211, 211, 213)', 'rgb(67, 67, 67)', 'rgb(116, 115, 116)', 'https://soundcloud.com/ennasound/sets/generation-omega/', 'ENNA_Generation_Omega_EP.zip', 29),
(9, 22, 'Clockwork Universe EP', '<p>Clockwork Universe ist die erste EP der Band - und alleine das Intro schreit nach einer grandioses Live-Performance.<br />\r\nDie erst seit 2015 existierende Band &uuml;berzeugt hier in f&uuml;nf Songs mit einpr&auml;gsamen Refrains, interessanten Themen und sehr hoher Qualit&auml;t.</p>', '2017-06-30', 'clockwork-universe', 7, 'rgb(97, 0, 0)', 'rgb(255, 255, 255)', '#CBCDE4', 'https://soundcloud.com/suburbian-rex/sets/clockwork-universe/s-UxOT8', 'Suburbian_Rex-Clockwork_Universe-EP.zip', 12),
(10, 25, 'Glück In Moll', '<p>Vollst&auml;ndig von Aapman&nbsp;selbst produziert gl&auml;nzt die &quot;Gl&uuml;ck In Moll&quot; EP sowohl&nbsp;im Schein der Untergrund-Hip-Hop Stra&szlig;enlaternen, als auch im Rampenlicht eines Musikers, der seit dem Alter&nbsp;von 5 Jahren am Klavier sitzt.<br />\r\nRapskills und Flow, Gesang und ausgereifte Melodien verschmelzen&nbsp;die acht Songs zu einem wundersch&ouml;n einheitlichen Gesamtwerk, das sowohl hartn&auml;ckige Rapfans als auch Musikbegeisterte aus anderen Genres zum Kopfnicken bringen sollte.</p>', '2017-08-12', 'glueck-in-moll', 8, '#000000', '#A9A99B', '#814200', 'https://soundcloud.com/aapman/sets/gluck-in-moll', 'Aapman-Glueck_in_Moll.zip', 7),
(13, 31, 'Miss you, Mate Beattape', '<p>Mit Old-School Beats und Samples, Nostalgie und Natur,&nbsp;Bilder von Grammofonen, die einem Instinktiv vor dem inneren Auge erscheinen verf&uuml;hrt einen Kokee&#39;s Beattape an einen Ort irgendwo zwischen Weltall und dem&nbsp;verregneten Hamburger Hafen.<br />\r\nAn einen Ort, an dem man sehr gerne eine halbe Stunde nichts anderes tut, als der Musik zu lauschen und ihre Botschaft aufzunehmen.</p>', '2017-09-07', 'miss-you-mate-beattape', 9, '#000000', '#810041', '#AB7491', 'https://soundcloud.com/kokee-thornton/sets/kokee-thornton-miss-you-mate-beattape-2017-cutversion', 'Kokee_Thornton-Miss_you_Mate_Beattape.zip', 12),
(14, 32, 'Grün & Bunt EP', '<p>Wenn INVO sich die Zeit nimmt, seiner Kreativit&auml;t freien Lauf zu lassen, dann wird es gr&uuml;n, dann&nbsp;wird es bunt. Dann&nbsp;wird sich zur&uuml;ckgelehnt und auf perfekt schmutzigen Old-School Beats geflowt.<br />\r\nDieses Mal haben auch andere K&uuml;nstler ihren Weg auf die EP gefunden, schlie&szlig;lich sei das zusammenarbeiten und gemeinsam kreativ sein einer der Gr&uuml;nde, warum INVO Musik macht.</p>', '2017-09-17', 'gruen-und-bunt', 3, '#DADADA', '#121513', '#DA0035', 'https://soundcloud.com/invorap/sets/gruen-und-bunt', 'INVO-Gruen_und_Bunt_EP.zip', 15),
(15, 35, 'Drawbacks Of Being An Astronaut', '<p>Paul kommt Urspr&uuml;nglich aus der Sample-Hip-Hop Ecke, wo er sich an Soul und Jazz Lieder bedient, und diese zu ganz eigenen Meisterwerken verarbeitet hat.<br />\r\nDiese EP ist jedoch komplett selbst produziert, best&uuml;ckt mit selbst aufgenommenen Sounds und Instrumenten.<br />\r\nLo-Fi Drums und sanfte Kl&auml;nge, Raum erschaffende Atmosph&auml;ren und pr&auml;zise eingesetzte Samples machen aus einem &quot;simplen Beattape&quot; einen gro&szlig;artigen&nbsp;Epos.</p>', '2017-08-20', 'drawbacks-of-being-an-astronaut', 11, '#DCD4C9', '#362624', '#A9464C', 'https://soundcloud.com/paulmaerz/sets/drawbacks-of-being-an-astronaut', 'Paul_Maerz-Drawbacks-Of-Being-An-Astronaut.zip', 5),
(16, 38, 're:loom live concert', '<p>Diese&nbsp;Songs sind nur vier von den zahlreichen Unikaten, die re:loom jedes Mal live auf der B&uuml;hne kreieren.<br />\r\nEin Mix an elektronischen Sounds, mehrsprachigen Vocals und jeder Menge Kreativit&auml;t, aufgenommen auf einem Benefiz-Konzert&nbsp;in Neuss f&uuml;r eine Initiative f&uuml;r Kinder in Kolumbien.<br/><small>Foto (Cover): &copy;<a href="https://www.facebook.com/joerg.dembinski.photography/" rel="nofollow" target="_blank"> J&ouml;rg Dembinski</a></small></p>', '2017-09-01', 'neuss-live', 12, '#000000', '#e2e2e2', '#f0002d', 'https://soundcloud.com/reloom/sets/neuss-live', 'RELOOM-Neuss_Live.zip', 1),
(17, 41, 'Da Ist Mehr', '<p>Assim und CML verpacken auf dieser EP gute-Laune-Hymnen, aber auch ernste Themen, bespickt mit&nbsp;bunten Metaphern, eing&auml;ngigen Hooks und sprachlichen Finessen.<br />\r\nSchon beim ersten h&ouml;ren wird zum Takt genickt, jeder weitere Durchlauf offenbart mehr und mehr das Gesamtbild.<br />\r\nBeats von talentierten Produzenten und <a href="https://www.youtube.com/watch?v=n8Jm7HH8BVo" rel="nofollow" target="_blank">beeindruckende&nbsp;Musik-Videos</a> runden das Ganze wunderbar ab.</p>', '2017-04-09', 'da-ist-mehr', 13, '#000000', '#ffffff', '#ffff00', 'https://soundcloud.com/s1mrecords/sets/da-ist-mehr', 'CML_und_Assim-Da_ist_mehr.zip', NULL),
(18, 44, 'Jabaroo', '<p>Jabaroo erschaffen mit diesem Album Klangwelten, bei denen man sich perfekt zur&uuml;cklehnen und sie auf sich einwirken lassen kann.<br />\r\nWie die vorbeiziehende Landschaft unter den Fl&uuml;geln des&nbsp;&nbsp;<a href="https://www.youtube.com/watch?time_continue=38&amp;v=kI0dT5yDj9E" rel="nofollow" target="_blank">&quot;Vogels, der fliegt und fliegt&quot;</a>&nbsp;entwickeln sich Atmosph&auml;ren und Melodien auf eine&nbsp;so wunderbar zeitlos und naturverbundene Art und Weise.</p>', '2013-08-30', 'jabaroo-album', 14, '#3B3A3F', '#B3BDB6', '#95B3D4', 'https://soundcloud.com/jabaroo/sets/jabaroo', 'Jabaroo-Jabaroo.zip', 3);
